
const leaderboard = document.getElementById('leaderboard');
const sparksContainer = document.createElement('div');
sparksContainer.classList.add('sparks-container');
document.body.appendChild(sparksContainer);
const students = [
    { name: 'Mayank Nikesh Borkar', score: 95, animeImage: 'data:image/jpeg;base64,' }, // Replace with actual anime images
    { name: 'Nishant Datta', score: 90, animeImage: 'data:image/jpeg;base64,' },
    { name: 'Anuj Kumar Gaud', score: 85, animeImage: 'data:image/jpeg;base64,' },
    { name: 'Ayyan Shaikh', score: 92, animeImage: 'data:image/jpeg;base64,' },
    { name: 'Nikhil Munda', score: 88, animeImage: 'data:image/jpeg;base64,' }
];
function createSpark() {
    const spark = document.createElement('div');
    spark.classList.add('spark');
    const size = Math.random() * 20 + 10; // Random size between 10px and 30px
    spark.style.width = `${size}px`;
    spark.style.height = `${size}px`;
    spark.style.left = `${Math.random() * window.innerWidth}px`;
    spark.style.top = `${Math.random() * window.innerHeight}px`;
    sparksContainer.appendChild(spark);

    setTimeout(() => {
        spark.remove(); // Remove spark after animation ends
    }, 1000);
}


function updateLeaderboard() {
    leaderboard.innerHTML = '';
    students.sort((a, b) => b.score - a.score);
    students.forEach(student => {
        const card = document.createElement('div');
        card.classList.add('card');
        
        const crownImg = document.createElement('img');
        crownImg.src = 'https://img.icons8.com/ios-filled/50/00bfff/crown.png'; // Crown image
        crownImg.classList.add('crown');
        
        const cardContent = document.createElement('div');
        cardContent.classList.add('card-content');

        const name = document.createElement('div');
        name.classList.add('name');
        name.textContent = student.name;
        
        const score = document.createElement('div');
        score.classList.add('score');
        score.textContent = `Score: ${student.score}`;
        
        const animeImg = document.createElement('img');
        animeImg.src = student.animeImage;
        animeImg.classList.add('anime-image');

        cardContent.appendChild(name);
        cardContent.appendChild(score);
        cardContent.appendChild(animeImg);
        card.appendChild(crownImg);
        card.appendChild(cardContent);
        leaderboard.appendChild(card);
    });
}

